import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useUser } from "../context/UserContext";

const Login = () => {
  const [userlogin, setuserlogin] = useState([]);
  const navigate = useNavigate();
  const { setIsStaff } = useUser();

  const completelogin = async () => {
    const response = await fetch("/applicant/token/", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(userlogin),
    });

    if (response.ok) {
      const data = await response.json();
      localStorage.setItem("access_token", data.access);
      localStorage.setItem("refresh_token", data.refresh);

      const staffResponse = await fetch("/applicant/retrieve-staff-status", {
        method: "GET",
        headers: {
          Authorization: `Bearer ${data.access}`,
          "Content-Type": "application/json",
        },
      });

      if (staffResponse.ok) {
        const staffData = await staffResponse.json();
        setIsStaff(staffData.is_staff); // Set is_staff in context

        if (staffData.is_staff === true) {
          navigate("/employee/home/");
        } else {
          navigate("/");
        }
        window.location.reload();
      }
    }
  };

  const input = (e) => {
    const { name, value } = e.target;
    setuserlogin({
      ...userlogin,
      [name]: value,
    });
  };

  const submit = (e) => {
    e.preventDefault();
    completelogin();
  };

  return (
    <form className="container-sm w-25" onSubmit={submit}>
      <div className="mb-3">
        <label htmlFor="username" className="form-label">
          Username
        </label>
        <input
          type="text"
          className="form-control"
          id="username"
          name="username"
          onChange={input}
        />
      </div>
      <div className="mb-3">
        <label htmlFor="password" className="form-label">
          Password
        </label>
        <input
          type="password"
          className="form-control"
          id="password"
          name="password"
          onChange={input}
        />
      </div>
      <button type="submit" className="btn btn-primary">
        Login
      </button>
    </form>
  );
};

export default Login;
