import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import api from "../api";

const JobDetails = () => {
  const { jobId } = useParams();
  const [job, setJob] = useState([]);
  const [isStaff, setIsStaff] = useState(false);
  const [ApplicantDetails, setApplicantDetails] = useState([]);
  const [PrimarySkillApplicants, setPrimarySkillApplicants] = useState([]);

  useEffect(() => {
    if (job.jobprimaryskill || job.jobsecondaryskill) {
      getApplicants();
    }
  }, [job.jobprimaryskill, job.jobsecondaryskill]);

  const getApplicants = () => {
    api
      .get("/applicant/applicant/list/")
      .then(console.log("Applicants retrieved"))
      .then((response) => response.json())
      .then((data) => {
        const filteredApplicants = data.filter(
          (applicant) =>
            applicant.skills.includes(job.jobprimaryskill) ||
            applicant.skills.includes(job.jobsecondaryskill)
        );
        setApplicantDetails(filteredApplicants);

        const primarySkillApplicants = data.filter(
          (applicant) =>
            applicant.skill_1 === job.jobprimaryskill ||
            applicant.skill_2 === job.jobprimaryskill ||
            applicant.skill_3 === job.jobprimaryskill ||
            applicant.skill_4 === job.jobprimaryskill ||
            applicant.skill_5 === job.jobprimaryskill
        );
        setPrimarySkillApplicants(primarySkillApplicants);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  useEffect(() => {
    const retrievestaffstatus = async () => {
      const token = localStorage.getItem("access_token");
      if (token) {
        const response = await fetch("/applicant/retrieve-staff-status", {
          method: "GET",
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        });

        if (response.ok) {
          const data = await response.json();
          setIsStaff(data.is_staff);
        }
      }
    };
    retrievestaffstatus();
  }, []);

  const getJob = () => {
    fetch(`/Jobs/List/${jobId}`)
      .then((response) => response.json())
      .then((data) => {
        setJob(data);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  useEffect(() => {
    if (jobId) {
      getJob();
    }
  }, [jobId]);

  return (
    <div className="card">
      <div className="card-header">
        {job.jobtitle} - Job at {job.companyname}
      </div>
      <div className="card-body">
        <p className="card-text">Job Type: {job.jobtype}</p>
        <p className="card-text">Salary: £{job.salary}</p>
        <p className="card-text">Job Description: {job.jobdescription}</p>
        <p className="card-text">Location: {job.location}</p>
        <p className="card-text">Date listing was posted: {job.dateposted}</p>
        <p className="card-text">Deadline to apply: {job.deadline}</p>
        <p className="card-text">
          Sought skills: {job.jobprimaryskill}, {job.jobsecondaryskill}
        </p>
        <ul>
            {ApplicantDetails.map(applicant => (
              <li key={applicant.email}>{applicant.email}</li>
            ))}
        </ul>
        <div className="card mt-3">
          <div className="card-header">
            Applicants with Primary Skill: {job.jobprimaryskill}
          </div>
          <div className="card-body">
            <ul>
              {PrimarySkillApplicants.map(applicant => (
                <li key={applicant.email}>{applicant.email}</li>
              ))}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default JobDetails;
