import React, {useEffect, useState} from 'react'
import Authorisedroute from '../components/Authorisedroute'

import api from '../api'

const ApplicantDetails = () => {

  const [applicantDetails, setApplicantDetails] = useState([])
  const [fullname, setfullname] = useState('')
  const [email, setemail] = useState('')
  const [phonenumber, setphonenumber] = useState('')
  const [skill_1, setskill_1] = useState('')
  const [skill_2, setskill_2] = useState('')
  const [skill_3, setskill_3] = useState('')
  const [skill_4, setskill_4] = useState('')
  const [skill_5, setskill_5] = useState('')
  const [qualifications, setqualifications] = useState('')
  const [preferences, setpreferences] = useState('')
  const [cv, setcv] = useState('')

  useEffect(() => {
    getApplicantDetails()
  }, [])

  const deleteApplicantDetails = (id) => {
    api.delete(`/applicant/details/${id}`).then((res) => {
      if (res.status === 204) {
        alert('Applicant details deleted successfully')
        getApplicantDetails()
      } else {
        alert('Error deleting applicant details')
      }
    })
  }

  const sendApplicantDetails = (e) => {
    e.preventDefault()
    api.post("/applicant/details/", {fullname, email, phonenumber, skill_1, skill_2, skill_3, skill_4, skill_5, qualifications, preferences, cv}).then((res) => {
      if (res.status === 201) {
        alert('Applicant details sent successfully')
        getApplicantDetails()
      } else {
        alert('Error sending applicant details')
      }
    })

  }

  const getApplicantDetails = async () => {
    api
      .get('/applicant/details/')
      .then((res) => res.data)
      .then((data) => {
        setApplicantDetails(data)
        console.log(data)
      })
      .catch((err) => alert(err))
  }

  return (
    <Authorisedroute>
      <div>ApplicantDetails</div>
    </Authorisedroute>
  )
}

export default ApplicantDetails
