import React, { useState, useEffect } from "react";

function ApplicantDetails() {
  const [details, setdetails] = useState([]);
  const [fullname, setfullname] = useState("");
  const [email, setemail] = useState("");
  const [phonenumber, setphonenumber] = useState("");
  const [skill_1, setskill_1] = useState("");
  const [skill_2, setskill_2] = useState("");
  const [skill_3, setskill_3] = useState("");
  const [skill_4, setskill_4] = useState("");
  const [skill_5, setskill_5] = useState("");
  const [qualifications, setqualifications] = useState("");
  const [preferences, setpreferences] = useState("");
  const [cv, setcv] = useState(null);
  const [token, setToken] = useState(null);

  useEffect(() => {
    const access_key = localStorage.getItem("access_token");
    setToken(access_key);
    getApplicantDetails(access_key);
  }, []);

  const getApplicantDetails = (access_key) => {
    fetch("/applicant/details/", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${access_key}`,
      },
    })
      .then((response) => response.json())
      .then((data) => {
        setdetails(data);
        setfullname(data.fullname);
        setemail(data.email);
        setphonenumber(data.phonenumber);
        setskill_1(data.skill_1);
        setskill_2(data.skill_2);
        setskill_3(data.skill_3);
        setskill_4(data.skill_4);
        setskill_5(data.skill_5);
        setqualifications(data.qualifications);
        setpreferences(data.preferences);
      });
  };

  const SubmitDetails = async (e) => {
    e.preventDefault();
    console.log("Details submitted");
    const formData = new FormData();
    formData.append("fullname", fullname);
    formData.append("email", email);
    formData.append("phonenumber", phonenumber);
    formData.append("skill_1", skill_1);
    formData.append("skill_2", skill_2);
    formData.append("skill_3", skill_3);
    formData.append("skill_4", skill_4);
    formData.append("skill_5", skill_5);
    formData.append("qualifications", qualifications);
    formData.append("preferences", preferences);
    formData.append("cv", cv);

    try {
      const response = await fetch("/applicant/details/", {
        method: "POST",
        headers: {
          Authorization: `Bearer ${token}`,
        },
        body: formData,
      });
      const data = await response.json();
      if (response.ok) {
        alert("Details submitted successfully");
        getApplicantDetails();
      } else {
        alert("Failed to submit details");
        console.error("Error updating details", data.error);
      }
    } catch (error) {
      console.error("Error updating details", error);
    }
  };
  const deleteApplicantDetails = (id) => {
    fetch(`/applicant/details/delete/${id}/`, {
      method: "DELETE",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
    })
      .then((response) => {
        if (response.status === 204) alert("Details deleted successfully");
        else alert("Error deleting details");
        getApplicantDetails();
      })
      .catch((error) => alert(error));
  };

  return (
    <div>
      <div className="applicantdetails-section">
        <h2>Applicant Details</h2>

        {details.map((applicantdetails) => (
          <ApplicantDetails
            applicantdetails={applicantdetails}
            onDelete={deleteApplicantDetails}
            key={applicantdetails.id}
          />
        ))}
      </div>
      <h1>Applicant Details</h1>
      <p>Full Name: {fullname}</p>
      <p>Email: {email}</p>
      <p>Phone Number: {phonenumber}</p>
      <p>Skill 1: {skill_1}</p>
      <p>Skill 2: {skill_2}</p>
      <p>Skill 3: {skill_3}</p>
      <p>Skill 4: {skill_4}</p>
      <p>Skill 5: {skill_5}</p>
      <p>Qualifications: {qualifications}</p>
      <p>Preferences: {preferences}</p>
      <form onSubmit={SubmitDetails}>
        <input
          type="text"
          placeholder="Full Name"
          value={fullname}
          onChange={(e) => setfullname(e.target.value)}
        />
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setemail(e.target.value)}
        />
        <input
          type="text"
          placeholder="Phone Number"
          value={phonenumber}
          onChange={(e) => setphonenumber(e.target.value)}
        />
        <input
          type="text"
          placeholder="Skill 1"
          value={skill_1}
          onChange={(e) => setskill_1(e.target.value)}
        />
        <input
          type="text"
          placeholder="Skill 2"
          value={skill_2}
          onChange={(e) => setskill_2(e.target.value)}
        />
        <input
          type="text"
          placeholder="Skill 3"
          value={skill_3}
          onChange={(e) => setskill_3(e.target.value)}
        />
        <input
          type="text"
          placeholder="Skill 4"
          value={skill_4}
          onChange={(e) => setskill_4(e.target.value)}
        />
        <input
          type="text"
          placeholder="Skill 5"
          value={skill_5}
          onChange={(e) => setskill_5(e.target.value)}
        />
        <input
          type="text"
          placeholder="Qualifications"
          value={qualifications}
          onChange={(e) => setqualifications(e.target.value)}
        />
        <input
          type="text"
          placeholder="Preferences"
          value={preferences}
          onChange={(e) => setpreferences(e.target.value)}
        />
        <input
          type="file"
          accept=".pdf"
          onChange={(e) => setcv(e.target.files[0])}
        />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default ApplicantDetails;
