import React, { useEffect, useState } from "react";
import Authorisedroute from "../components/Authorisedroute";
import DetailsDisplay from "../webpages/DetailsDisplay";
import api from "../api";

const ApplicantProfile = () => {
  const [applicantDetails, setApplicantDetails] = useState([]);

  useEffect(() => {
    getApplicantDetails();
  }, []);

  const deleteApplicantDetails = (id) => {
    api.delete(`/applicant/details/delete/${id}/`).then((res) => {
      if (res.status === 204) {
        alert("Applicant details deleted successfully");
        getApplicantDetails();
      } else {
        alert("Error deleting applicant details");
      }
    });
  };


  const getApplicantDetails = async () => {
    api
      .get("/applicant/details/")
      .then((res) => res.data)
      .then((data) => {
        setApplicantDetails(data);
        console.log(data);
      })
      .catch((err) => alert(err));
  };

  return (
    <Authorisedroute>
      <div className="card">
        <div className="card-body">
          <h5 className="card-title">Your Profile:</h5>
          <div className="list-group">
            {applicantDetails.map((applicantDetails) => (
              <DetailsDisplay
                applicantdetails={applicantDetails}
                onDelete={deleteApplicantDetails}
                key={applicantDetails.id}
              />
            ))}
          </div>
          <a href="/applicant/details/update/" className="btn btn-primary mt-3">Add details</a>
        </div>
      </div>
    </Authorisedroute>
  );
};

export default ApplicantProfile;
