import { useState, useEffect, useCallback } from 'react';
import {jwtDecode} from 'jwt-decode';
import api from '../api'; // Assuming you have an api module for making requests
import { ACCESS_TOKEN, REFRESH_TOKEN } from '../constants'; // Assuming you have constants defined for tokens

function Authorisedroute({children}) {
    const [authorised, setAuthorised] = useState(null);

    const auth = useCallback(async () => {
        const token = localStorage.getItem(ACCESS_TOKEN);
        if (!token) {
            setAuthorised(false);
            return;
        }
        const jwtdecode = jwtDecode(token);
        const expiryoftoken = jwtdecode.exp;
        const now = Date.now() / 1000;

        if (expiryoftoken < now) {
            await refreshtoken();
        } else {
            setAuthorised(true);
        }
    }, []);

    const refreshtoken = useCallback(async () => {
        const refreshtoken = localStorage.getItem(REFRESH_TOKEN);
        try {
            const res = await api.post('/applicant/token/refresh/', {refresh: refreshtoken});
            if (res.status === 200) {
                const {access} = res.data;
                localStorage.setItem(ACCESS_TOKEN, access);
                setAuthorised(true);
            } else {
                setAuthorised(false);
            }
        } catch {
            setAuthorised(false);
            console.log("Error refreshing token");
        }
    }, []);

    useEffect(() => {
        auth().catch(() => setAuthorised(false));
    }, [auth]);

    if (authorised === null) {
        return <div>Loading...</div>;
    }

    return authorised ? children : <div>Not authorised</div>;
}

export default Authorisedroute;
