import React, { useEffect, useState } from 'react';

const JobDetails = ({ jobId }) => {
  const [job, setJob] = useState({});
  const [applicants, setApplicants] = useState([]);

  const getJob = () => {
    fetch(`/Jobs/List/${jobId}`)
      .then(response => response.json())
      .then(data => {
        setJob(data);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const getApplicants = () => {
    fetch(`/Applicants/List`)
      .then(response => response.json())
      .then(data => {
        const filteredApplicants = data.filter(applicant => 
          applicant.skills.includes(job.jobprimaryskill) || 
          applicant.skills.includes(job.jobsecondaryskill)
        );
        setApplicants(filteredApplicants);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  useEffect(() => {
    if (jobId) {
      getJob();
    }
  }, [jobId]);

  useEffect(() => {
    if (job.jobprimaryskill || job.jobsecondaryskill) {
      getApplicants();
    }
  }, [job.jobprimaryskill, job.jobsecondaryskill]);

  return (
    <div className="card">
      <div className="card-header">{job.jobtitle} - Job at {job.companyname}</div>
      <div className="card-body">
        <p className="card-text">
          Job Type: {job.jobtype}
        </p>
        <p className="card-text">
          Salary: Â£{job.salary}
        </p>
        <p className="card-text">
          Job Description: {job.jobdescription}
        </p>
        <p className="card-text">
          Location: {job.location}
        </p>
        <p className="card-text">
          Date listing was posted: {job.dateposted}
        </p>
        <p className="card-text">
          Deadline to apply: {job.deadline}
        </p>
        <p className="card-text">
          Sought skills: {job.jobprimaryskill}, {job.jobsecondaryskill}
        </p>
        <div className="applicants">
          <h5>Applicants:</h5>
          <ul>
            {applicants.map(applicant => (
              <li key={applicant.email}>{applicant.email}</li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default JobDetails;
