import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";

const JobDetails = () => {
  const { jobId } = useParams();
  console.log("useParams output:", useParams());
  console.log("this is the jobid", jobId);

  const [job, setJob] = useState([]);
  const getJob = () => {
    fetch(`/Jobs/${jobId}/`)
      .then((response) => response.json())
      .then((data) => {
        setJob(data);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  useEffect(() => {
    if (jobId) {
      getJob();
    }
  }, [jobId]);

  return (
    <div className="card">
      <div className="card-header">{job.title}</div>
      <div className="card-body">
        <p className="card-text">{job.jobdescription}</p>
      </div>
    </div>
  );
};

export default JobDetails;
