import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import 'bootstrap/dist/css/bootstrap.min.css';

const JobDetails = () => {
  const { jobId } = useParams();
  const [job, setJob] = useState({});
  const [isStaff, setIsStaff] = useState(false);
  const [applicants, setApplicants] = useState([]);

  useEffect(() => {
    const retrievestaffstatus = async () => {
      const token = localStorage.getItem("access_token");
      if (token) {
        const response = await fetch("/applicant/retrieve-staff-status", {
          method: "GET",
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        });

        if (response.ok) {
          const data = await response.json();
          setIsStaff(data.is_staff);
        }
      }
    }
    retrievestaffstatus();
  }, []);

  const getJob = () => {
    fetch(`/Jobs/List/${jobId}`)
      .then(response => response.json())
      .then(data => {
        setJob(data);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const getApplicants = () => {
    fetch(`/applicants/skills/`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        primarySkill: job.jobprimaryskill,
        secondarySkill: job.jobsecondaryskill,
      }),
    })
      .then(response => response.json())
      .then(data => {
        setApplicants(data);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  useEffect(() => {
    if (jobId) {
      getJob();
    }
  }, [jobId]);

  useEffect(() => {
    if (isStaff && job.jobprimaryskill && job.jobsecondaryskill) {
      getApplicants();
    }
  }, [isStaff, job]);

  return (
    <div className="container">
      <div className="row">
        <div className="col-md-6">
          <div className="card">
            <div className="card-header">{job.jobtitle} - Job at {job.companyname}</div>
            <div className="card-body">
              <p className="card-text">
                Job Type: {job.jobtype}
              </p>
              <p className="card-text">
                Salary: Â£{job.salary}
              </p>
              <p className="card-text">
                Job Description: {job.jobdescription}
              </p>
              <p className="card-text">
                Location: {job.location}
              </p>
              <p className="card-text">
                Date listing was posted: {job.dateposted}
              </p>
              <p className="card-text">
                Deadline to apply: {job.deadline}
              </p>
              <p className="card-text">
                Sought skills: {job.jobprimaryskill}, {job.jobsecondaryskill}
              </p>
            </div>
          </div>
        </div>
        {isStaff && (
          <div className="col-md-6">
            <div className="card">
              <div className="card-header">Applicants</div>
              <div className="card-body">
                <ul className="list-group">
                  {applicants.map((applicant, index) => (
                    <li key={index} className="list-group-item">
                      {applicant.email}
                    </li>
                  ))}
                </ul>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default JobDetails;
