import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const ListofJobs = () => {
  const [jobs, setJobs] = useState([]);
  const getJobs = () => {
    fetch("/Jobs/List/")
      .then((response) => response.json())
      .then((data) => {
        setJobs(data);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  useEffect(() => {
    getJobs();
  }, []);

  const width = {
    width: "18rem",
  };

  return (
    <div className="card-group">
      <ul className="card-group">
        {jobs.map((job, index) => (
          <li key={index} className="card-group">
            <div className="card" style={width}>
              <div className="card-body">
                <h5 className="card-title">{job.jobtitle}</h5>
                <p className="card-text">
                  {job.jobdescription}
                </p>
                <Link className="btn btn-primary" to={`/Jobs/List/${job.id}`}>{job.jobtitle}</Link>
                <div className="card-footer">
                  <small className="text-body-secondary">Last updated {job.dateposted}</small>
                </div>
              </div>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ListofJobs;
