import { BrowserRouter, Routes, Route } from "react-router-dom";
import {GoogleOAuthProvider} from '@react-oauth/google'
import "./App.css";
import Header from "./components/Header";
import ListofJobs from "./webpages/ListofJobs";
import JobDetails from "./webpages/JobDetails";
import HomePage from "./webpages/HomePage";
import { useEffect, useState } from "react";
import ApplicantContext from "./context/ApplicantContext";

function App() {
  const clientId = process.env.REACT_APP_CLIENT_ID
  const [userinformation, setuserinformation] = useState([])

  const verifytoken = () => {
    const access_key = localStorage.getItem["access_token"]
    const username = localStorage.getItem["username"]

    fetch('/user/g-sso/token/verify', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({token: access_key})
    })
    .then((response) =>{
      if (response.ok){
        setuserinformation({...userinformation, access_token:access_key, username:username})
      }
      else {
        setuserinformation({...userinformation, access_token:null, username:null})
      }
    })
  }

  useEffect(() => {
    verifytoken()
  }, [])

  return (
    <BrowserRouter>
    <GoogleOAuthProvider clientId={clientId}>
      <div className="App">
        <Header />
        <Routes>
          <Route path="/Jobs/List/" exact element={<ListofJobs />}></Route>
          <Route path="/Jobs/List/:jobId/" element={<JobDetails />}></Route>
          <Route path="/" exact element={<HomePage />}></Route>
        </Routes>
      </div>
      </GoogleOAuthProvider>
    </BrowserRouter>
  );
}

export default App;
