import { useState, useEffect } from "react";
import api from "../api";
import DetailsDisplay from "./DetailsDisplay";
import { ACCESS_TOKEN } from "../constants";

function Details() {
  const [applicantDetails, setApplicantDetails] = useState([]);
  const [fullname, setfullname] = useState("");
  const [email, setemail] = useState("");
  const [phonenumber, setphonenumber] = useState("");
  const [skill_1, setskill_1] = useState("");
  const [skill_2, setskill_2] = useState("");
  const [skill_3, setskill_3] = useState("");
  const [skill_4, setskill_4] = useState("");
  const [skill_5, setskill_5] = useState("");
  const [qualifications, setqualifications] = useState("");
  const [preferences, setpreferences] = useState("");
  const [cv, setcv] = useState(null);
  const [token, setToken] = useState(null);

  useEffect(() => {
    const storedToken = localStorage.getItem(ACCESS_TOKEN);
    setToken(storedToken);
    getApplicantDetails();
  }, []);

  const getApplicantDetails = () => {
    api
      .get("/applicant/details/", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((res) => res.data)
      .then((data) => {
        setApplicantDetails(data);
        console.log(data);
      })
      .catch((err) => alert(err));
  };

  const deleteApplicantDetails = (id) => {
    api
      .delete(`/applicant/details/delete/${id}/`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((res) => {
        if (res.status === 204) alert("Details deleted!");
        else alert("Error, failed to delete details!");
        getApplicantDetails();
      })
      .catch((error) => alert(error));
  };

  const API_BASE_URL = "http://127.0.0.1:8000";
  useEffect(() => {
    if (token) {
      getApplicantDetails();
    }
  }, [token]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log("Form submitted");
    console.log("Token:", token);
    const formData = new FormData();
    formData.append("fullname", fullname);
    formData.append("email", email);
    formData.append("phonenumber", phonenumber);
    formData.append("skill_1", skill_1);
    formData.append("skill_2", skill_2);
    formData.append("skill_3", skill_3);
    formData.append("skill_4", skill_4);
    formData.append("skill_5", skill_5);
    formData.append("qualifications", qualifications);
    formData.append("preferences", preferences);
    formData.append("cv", cv);

    try {
      console.log(token);
      const response = await fetch(`${API_BASE_URL}/applicant/details/`, {
        method: "POST",
        body: formData,
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      const data = await response.json();
      if (response.ok) {
        alert("Details updated!");
        getApplicantDetails();
      } else {
        alert("Failed to update details!");
        console.error("Error updating applicant details:", data.error);
      }
    } catch (error) {
      console.error("Error updating applicant details:", error);
    }
  };

  return (
    <div className="container">
      <div className="applicantdetails-section">
        <h2>Applicant Details</h2>

        {applicantDetails.map((applicantdetails) => (
          <DetailsDisplay
            applicantdetails={applicantdetails}
            onDelete={deleteApplicantDetails}
            key={applicantdetails.id}
          />
        ))}
      </div>
      <div className="form-container">
        <h2>Update Applicant Details</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="fullname">First and Last Name:</label>
            <br />
            <input
              type="text"
              id="fullname"
              name="fullname"
              required
              onChange={(e) => setfullname(e.target.value)}
              value={fullname}
            />
          </div>
          <div className="form-group">
            <label htmlFor="email">Email:</label>
            <br />
            <input
              type="email"
              id="email"
              name="email"
              required
              onChange={(e) => setemail(e.target.value)}
              value={email}
            />
          </div>
          <div className="form-group">
            <label htmlFor="phonenumber">Phone Number:</label>
            <br />
            <input
              type="tel"
              id="phonenumber"
              name="phonenumber"
              required
              pattern="^(\+\d{2})?\s?\d{10}$"
              onChange={(e) => setphonenumber(e.target.value)}
              value={phonenumber}
            />
          </div>
          <div className="form-group">
            <label htmlFor="skill_1">Skill 1:</label>
            <br />
            <select
              id="skill_1"
              name="skill_1"
              required
              onChange={(e) => setskill_1(e.target.value)}
              value={skill_1}
            >
              <option value="">Select a skill</option>
              <option value="Problem-Solving">Problem-Solving</option>
              <option value="Leadership">Leadership</option>
              <option value="Time-Management">Time-Management</option>
              <option value="Communication">Communication</option>
              <option value="Collaboration">Collaboration</option>
              <option value="Adaptability">Adaptability</option>
              <option value="Creativity">Creativity</option>
              <option value="Empathy">Empathy</option>
              <option value="Negotiation">Negotiation</option>
              <option value="Critical-Thinking">Critical-Thinking</option>
            </select>
          </div>
          <div className="form-group">
            <label htmlFor="skill_2">Skill 2:</label>
            <br />
            <select
              id="skill_2"
              name="skill_2"
              required
              onChange={(e) => setskill_2(e.target.value)}
              value={skill_2}
            >
              <option value="">Select a skill</option>
              <option value="Problem-Solving">Problem-Solving</option>
              <option value="Leadership">Leadership</option>
              <option value="Time-Management">Time-Management</option>
              <option value="Communication">Communication</option>
              <option value="Collaboration">Collaboration</option>
              <option value="Adaptability">Adaptability</option>
              <option value="Creativity">Creativity</option>
              <option value="Empathy">Empathy</option>
              <option value="Negotiation">Negotiation</option>
              <option value="Critical-Thinking">Critical-Thinking</option>
            </select>
          </div>
          <div className="form-group">
            <label htmlFor="skill_3">Skill 3:</label>
            <br />
            <select
              id="skill_3"
              name="skill_3"
              required
              onChange={(e) => setskill_3(e.target.value)}
              value={skill_3}
            >
              <option value="">Select a skill</option>
              <option value="Problem-Solving">Problem-Solving</option>
              <option value="Leadership">Leadership</option>
              <option value="Time-Management">Time-Management</option>
              <option value="Communication">Communication</option>
              <option value="Collaboration">Collaboration</option>
              <option value="Adaptability">Adaptability</option>
              <option value="Creativity">Creativity</option>
              <option value="Empathy">Empathy</option>
              <option value="Negotiation">Negotiation</option>
              <option value="Critical-Thinking">Critical-Thinking</option>
            </select>
          </div>
          <div className="form-group">
            <label htmlFor="skill_4">Skill 4:</label>
            <br />
            <select
              id="skill_4"
              name="skill_4"
              required
              onChange={(e) => setskill_4(e.target.value)}
              value={skill_4}
            >
              <option value="">Select a skill</option>
              <option value="Problem-Solving">Problem-Solving</option>
              <option value="Leadership">Leadership</option>
              <option value="Time-Management">Time-Management</option>
              <option value="Communication">Communication</option>
              <option value="Collaboration">Collaboration</option>
              <option value="Adaptability">Adaptability</option>
              <option value="Creativity">Creativity</option>
              <option value="Empathy">Empathy</option>
              <option value="Negotiation">Negotiation</option>
              <option value="Critical-Thinking">Critical-Thinking</option>
            </select>
          </div>
          <div className="form-group">
            <label htmlFor="skill_5">Skill 5:</label>
            <br />
            <select
              id="skill_5"
              name="skill_5"
              required
              onChange={(e) => setskill_5(e.target.value)}
              value={skill_5}
            >
              <option value="">Select a skill</option>
              <option value="Problem-Solving">Problem-Solving</option>
              <option value="Leadership">Leadership</option>
              <option value="Time-Management">Time-Management</option>
              <option value="Communication">Communication</option>
              <option value="Collaboration">Collaboration</option>
              <option value="Adaptability">Adaptability</option>
              <option value="Creativity">Creativity</option>
              <option value="Empathy">Empathy</option>
              <option value="Negotiation">Negotiation</option>
              <option value="Critical-Thinking">Critical-Thinking</option>
            </select>
          </div>
          <div className="form-group">
            <label htmlFor="Qualifications">Qualifications</label>
            <br />
            <input
              type="text"
              id="qualifications"
              name="qualifications"
              required
              onChange={(e) => setqualifications(e.target.value)}
              value={qualifications}
            />
          </div>
          <div className="form-group">
            <label htmlFor="Preferences">Job Preferences:</label>
            <br />
            <textarea
              id="preferences"
              name="preferences"
              required
              onChange={(e) => setpreferences(e.target.value)}
              value={preferences}
            />
          </div>
          <div className="form-group">
            <label htmlFor="cv">Upload a CV</label>
            <br />
            <input
              type="file"
              id="cv"
              name="cv"
              onChange={(e) => setcv(e.target.files[0])}
            />
          </div>
          <button type="submit" className="form-button">
            Update Details
          </button>
        </form>
      </div>
    </div>
  );
}

export default Details;
