import React, { useEffect, useState } from "react";
import Authorisedroute from "../components/Authorisedroute";
import DetailsDisplay from "../webpages/DetailsDisplay";
import api from "../api";

const ApplicantProfile = () => {
  const [applicantDetails, setApplicantDetails] = useState([]);

  useEffect(() => {
    getApplicantDetails();
  }, []);

  const deleteApplicantDetails = (id) => {
    api.delete(`/applicant/details/delete/${id}/`).then((res) => {
      if (res.status === 204) {
        alert("Applicant details deleted successfully");
        getApplicantDetails();
      } else {
        alert("Error deleting applicant details");
      }
    });
  };


  const getApplicantDetails = async () => {
    api
      .get("/applicant/details/")
      .then((res) => res.data)
      .then((data) => {
        setApplicantDetails(data);
        console.log(data);
      })
      .catch((err) => alert(err));
  };

  return (
    <Authorisedroute>
      <div className="container mt-5">
        <div className="row">
          <div className="col-md-6">
            <div className="accordion" id="applicantDetails">
              {applicantDetails.map((details, i) => (
                <div className="accordion-item" key={details.id}>
                  <h2 className="accordion-header">
                    <button
                      className="accordion-button"
                      type="button"
                      data-bs-toggle="collapse"
                      data-bs-target={`#collapse${i}`}
                    >
                      Your Application
                    </button>
                  </h2>
                  <div
                    id={`collapse${i}`}
                    className="accordion-collapse collapse"
                    data-bs-parent="#applicantDetails"
                  >
                    <div className="accordion-body">
                      <h5>Name</h5>
                      <p>{details.fullname}</p>
                      <h5>Email</h5>
                      <p>{details.email}</p>
                      <h5>Phone Number</h5>
                      <p>{details.phonenumber}</p>
                      <h5>Skills</h5>
                      <ul>
                        <li>{details.skill_1}</li>
                        <li>{details.skill_2}</li>
                        <li>{details.skill_3}</li>
                        <li>{details.skill_4}</li>
                        <li>{details.skill_5}</li>
                      </ul>
                      <h5>Qualifications</h5>
                      <p>{details.qualifications}</p>
                      <h5>Preferences</h5>
                      <p>{details.preferences}</p>
                      <h5>CV</h5>
                      <p>{details.cv}</p>
                      <button
                        className="btn btn-danger mt-3"
                        onClick={() => deleteApplicantDetails(details.id)}
                      >
                        Delete
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
            <a href="/applicant/details/update/" className="btn btn-primary mt-3">
              Add details
            </a>
          </div>
          <div className="col-md-6">
            <div className="card">
              <div className="card-body">
                <h5 className="card-title">Recruitment Process</h5>
                <p className="card-text">Your progress in the recruitment process:</p>
                <div className="progress">
                  <div
                    className="progress-bar"
                    role="progressbar"
                    style={{ width: `${calculateProgress(applicantDetails)}%` }}
                    aria-valuenow={calculateProgress(applicantDetails)}
                    aria-valuemin="0"
                    aria-valuemax="100"
                  >
                    {calculateProgress(applicantDetails)}%
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </Authorisedroute>
  );

const calculateProgress = (details) => {
  let filledFields = 0;
  const totalFields = 10; // Adjust this based on the actual number of fields

  if (details.fullname) filledFields++;
  if (details.email) filledFields++;
  if (details.phonenumber) filledFields++;
  if (details.skill_1) filledFields++;
  if (details.skill_2) filledFields++;
  if (details.skill_3) filledFields++;
  if (details.skill_4) filledFields++;
  if (details.skill_5) filledFields++;
  if (details.qualifications) filledFields++;
  if (details.preferences) filledFields++;
  if (details.cv) filledFields++;

  return (filledFields / totalFields) * 100;
};
};

export default ApplicantProfile;
